    public static function getRemoteLocationCode($default = 'us'){

        if(!function_exists('geoip_country_code_by_name')){

            return $default;
        }

        $cc = strtolower(@geoip_country_code_by_name(getenv('REMOTE_ADDR')));

        if(!isset(self::$items[$cc])){

            return $default;
        }
        
        return $cc;
    }

    public static function getItems($remoteCountryFirst = true, $remoteCountryDefault = 'us'){

        if(!function_exists('geoip_country_code_by_name') || !$remoteCountryFirst){

            return self::$items;
        }
        else{

            $cc = self::getRemoteLocationCode($remoteCountryDefault);

            if(!isset(self::$items[$cc])){

                return self::$items;
            }

            $items = self::$items;

            unset($items[$cc]);

            $items = array_merge(array($cc => self::$items[$cc]), $items);

            return $items;
        }
    }
